import React from "react"
import X from "../images/1.png"
import O from "../images/0.png"

const Winer =(props)=>{
   
    if(props.winer === "X"){
        return(
            <div className="winer">                
                <span>Player {props.name_X} WIns! with <img className="icon" src={X} alt="X"/> </span>                
            </div>
        )
    }else{
        return(
            <div className="winer">
                <h1>Player {props.name_0} WIns! with <img className="icon" src={O} alt="0"/> </h1>                
            </div>
        )
    }
    
}

export default Winer
