import React, {Component} from 'react';

import Header from "./Header"


class Game extends Component {
state = {
  gameStart : false,
}
startGame =()=>{
  this.setState(prevState =>{
    return{
      gameStart : !prevState.gameStart
    }
  })
}
  render(){
    return (
      <Header  gameStart={this.gameStart}/>
    );
  }
}

export default Game;
